	@Column(name = "code")
	private String code;
	@Column(name = "name")
	private String name;
	@Column(name = "gender")
	private String gender;
	@Column(name = "annualSalary")
	private String annualSalary;
	@Column(name = "dateOfBirth")
	private String dateOfBirth;
	
	

//	public int getId() {
//		return id;
//	}
//
